        IERC20 collateralToken,
        bytes32 parentCollectionId,
        bytes32 conditionId,
        uint[] calldata partition,
        uint amount
    ) public {
        _mergePositions(
            collateralToken,
            parentCollectionId,
            conditionId,
            partition,
            amount
        );
        collateralToken.transfer(msg.sender, amount);
    }

    // @notice Merges positions and receives the collateral tokens.
